(1, 'Agra', 'National Plastic Agra (Mango Wood)', '', '', 'An Agra chair in mango wood embodies timeless Indian craftsmanship, featuring intricate carvings, sturdy construction, and a natural finish that highlights the wood\'s unique grain. Perfect for adding a touch of cultural elegance to any space.', '', '', 'Agra', 'Agra-Mango-Wood', 16, 'Agra chairs-Mangowood-(45) Lifestyle.webp, Agra chairs-Mangowood-(45)-white bg.webp, Agra chairs-Mangowood-(front)-white bg.webp', NULL, '510', '612', '20', '', '', '40', 'Mango Wood', '#F2B66D', 'without arm tent', 1, '2024-07-20 06:26:21'),
(2, 'Agra', 'National Plastic Agra (Mat Brown)', '', '', 'Elevate your living space with the Agra Chair in a rich, matt brown finish. This expertly crafted piece combines modern design with timeless sophistication, making it the perfect addition to any contemporary interior. Crafted from high-quality materials for exceptional durability, the Agra Chair\'s sleek silhouette and plush upholstery offer unparalleled comfort, inviting you to sink in and relax.', '', '', 'Agra', 'Agra-Mat-Brown', 16, 'Agra chairs-MATT BROWN-(45) Lifestyle.webp, Agra chairs-MATT BROWN-(45)-white bg.webp, Agra chairs-MATT BROWN-(front)-white bg.webp', NULL, '510', '612', '20', '', '', '40', 'Mat Brown', '#563921', 'without arm tent', 1, '2024-07-20 06:26:21'),
(3, 'Agra', 'National Plastic Agra (MGR)', '', '', 'Introducing the Agra Chair MGR  a masterpiece of modern design that redefines comfort and style. Crafted with meticulous attention to detail, this chair seamlessly blends form and function, creating a stunning addition to any living space.', '', '', 'Agra', 'Agra-MGR', 16, 'Agra chairs-MGR-(45)-Lifestyle.webp, Agra chairs-MGR-(45)-white bg.webp, Agra chairs-MGR-(front)-white bg.webp', NULL, '510', '612', '20', '', '', '40', 'MGR', '#D7DCE1', 'without arm tent', 1, '2024-07-20 06:26:21'),
